import React, { useState } from "react";
import "./SearchForm.css";
import searchAndPlayVideo from "./searchAndPlayVideo";
import speechToText from "./speechToText"; // We'll create this function next
import VoiceInput from "./VoiceInput";

const SearchForm = () => {
  const [word, setWord] = useState("");
  const [videoTypes, setVideoTypes] = useState({
    Real: false,
    Animation: false,
    Skeleton: false,
  });

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setVideoTypes({ ...videoTypes, [value]: checked });
    } else {
      setWord(value);
    }
  };

  const handleSearch = () => {
    searchAndPlayVideo(word, videoTypes);
  };

  return (
    <div className="text">
      <form>
        <label htmlFor="word">Nhập từ cần tìm:</label>
        <input
          type="text"
          id="word"
          name="word"
          placeholder="Nhập từ vào đây"
          disabled={isListening}
          value={word + (word.length ? " " + textInput : textInput)}
          onChange={handleInputChange}
        />
        <label htmlFor="video-type">Chọn kiểu video muốn xem:</label>
        <br />
        {["Real", "Animation", "Skeleton"].map((type) => (
          <label key={type}>
            <input
              type="checkbox"
              className="video-type"
              name="video-type"
              value={type}
              checked={videoTypes[type]}
              onChange={handleInputChange}
            />
            {type}
          </label>
        ))}
        <br />
        <button type="button" onClick={handleSearch}>
          Tìm kiếm
        </button>
      </form>
    </div>
  );
};

export default SearchForm;
