const searchAndPlayVideo = (word, videoTypes) => {
  const inputWord = word.toLowerCase();
  const csvUrl = "1_1000_data.csv"; // Relative path to the CSV file
  const videoFolder = "./vidSke/";
  const aniFolder = "./animeQIPEDCVid/animeQIPEDCVid/1-200/";
  const realVideo = document.querySelector(".real");
  const animationVideo = document.querySelector(".animation");
  const skeletonVideo = document.querySelector(".skeleton");

  fetch(csvUrl)
    .then((response) => response.text())
    .then((text) => {
      const lines = text.split("\n");
      let videoUrl = "";
      let animationVideoPath = "";
      let skeletonVideoPath = "";

      const realChecked = videoTypes.Real;
      const animationChecked = videoTypes.Animation;
      const skeletonChecked = videoTypes.Skeleton;

      for (const line of lines) {
        const row = line.split(",");
        const word = row[0].toLowerCase();
        const videoId = row[2];

        if (word === inputWord) {
          if (realChecked) {
            videoUrl = row[1];
          }

          animationVideoPath = aniFolder + "stylized_" + videoId + ".mp4";
          skeletonVideoPath = videoFolder + videoId + ".webm";
          break;
        }
      }

      realVideo.src = realChecked ? videoUrl : "";
      animationVideo.src = animationChecked ? animationVideoPath : "";
      skeletonVideo.src = skeletonChecked ? skeletonVideoPath : "";

      realVideo.style.visibility = realChecked ? "visible" : "hidden";
      animationVideo.style.visibility = animationChecked ? "visible" : "hidden";
      skeletonVideo.style.visibility = skeletonChecked ? "visible" : "hidden";

      if (!videoUrl && !skeletonVideoPath) {
        alert("Không tìm thấy từ này trong từ điển.");
      }
    })
    .catch((error) => {
      console.error("Error fetching CSV file:", error);
    });
};

export default searchAndPlayVideo;
