import React from 'react';

function searchAndPlayVideo() {
  const csvUrl = "/1_1000_data.csv";
  const videoFolder = "/vidSke/";
  const aniFolder = "/animeQIPEDCVid/animeQIPEDCVid/1-200/";

  fetch(csvUrl)
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.text();
    })
    .then(text => {
      // Xử lý dữ liệu CSV và chơi video
      console.log(text); // Hoặc các xử lý khác
    })
    .catch(error => {
      console.error('Error fetching CSV file:', error);
    });
}

function App() {
  return (
    <div className="App">
      <button onClick={searchAndPlayVideo}>Search and Play Video</button>
    </div>
  );
}

export default App;
