import React, {useEffect} from 'react';
import speechToText from './speechToText';

const VoiceInput = ({ word, setWord }) => {
    const { isListening, transcript, startListening, stopListening } = speechToText({ continuous: true });

    /*
    const handleVoiceInput = () => {
        if (isListening) {
            stopListening();
        } else {
            startListening();
        }
    };
    */
    useEffect(() => {
        setWord(prevWord => prevWord + (transcript.length ? (prevWord.length ? " " : "") + transcript : ""));
    }, [transcript]);

    return (
        <div>
            <button type="button" onMouseDown={startListening} onMouseUp={stopListening}>
                    {isListening ? <img src="submit.jpg" /> : "Start"} Listening
            </button>
        </div>
    );
};

export default VoiceInput;
